classprops.imageurl:
eval(begin
				HTTP.HTTPMethod('GET','http://smsgate.kyivstar.ua/_sms/ajax/?callback=jsonCallback&lang=ru&_=' + VarToStr(DateTimeToUnix(Now())));
				Stored['mid'] := HTML.RegExp('"mid":"(.*?)"', 1);
				Result:= 'http://smsgate.kyivstar.ua/_sms/captcha/?mid=' + Stored['mid'] + '&r=' + '0.' + RandomString('0123456789',8);
			end;)

classprops.proccessimage:
eval(
							var
								x,y,srcy: integer;
							begin
								Img.SetSize(350,140);
								for y:=0 to 1 do
									for x:=0 to 4 do
									begin
										if srcy>=630 then break;
										Img.CopyRect(x*70,y*70,Bounds(0,srcy,70,70));
										srcy:=srcy+70;
									end;
							end;
						)

classprops.postdata:
eval(
						var
							i: integer;
							Code: string;
						begin
							//Сортируем цифры в коде
							Code := '';
							for i:=1 to 9 do
							begin
								if Pos(VarToStr(i), SMS['Code'])>0 then
									Code:=Code + VarToStr(i);
							end;

							PostData.Add('code','0'+SMS['Phone_Def']);
							PostData.Add('phone',SMS['Phone_Number']);
							PostData.Add('message',SMS['Text']);
							PostData.Add('is_cyrillic',iff(isAscii(SMS['Text']),'0','1'));
							PostData.Add('mid',Stored['mid']);
							PostData.Add('captcha',Code);
						end;
					)

classprops.cd_error:
eval(
				begin
					Result:='Ошибка подверждения доставки';
				end;
			)
